Item":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":71,"contextId":4,"type":"NMI_TYPE_SUBMISSIONS","titleLocaleKey":"about.submissions"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":267,"menuId":9,"menuItemId":71,"parentId":69,"seq":9},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},{"navigationMenuItem":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":73,"contextId":4,"type":"NMI_TYPE_PRIVACY","titleLocaleKey":"manager.setup.privacyStatement"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":268,"menuId":9,"menuItemId":73,"parentId":69,"seq":10},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},{"navigationMenuItem":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":74,"contextId":4,"type":"NMI_TYPE_CONTACT","titleLocaleKey":"about.contact"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":269,"menuId":9,"menuItemId":74,"parentId":69,"seq":11},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false}],"_data":{"id":264,"menuId":9,"menuItemId":69,"parentId":0,"seq":6},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false}],"_data":{"id":9,"title":"Primary Navigation Menu","areaName":"primary","contextId":4},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false}';